import React, { useState } from 'react';
import { MapContainer, TileLayer } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import Markers from './Markers';
import placesData from '../assets/data.json';
import { useLocation } from 'react-router-dom';
import { Modal, Button, Form } from 'react-bootstrap';

const MapView = () => {
    const { places } = placesData;
    const [state, setState] = useState({
        currentLocation: { lat: '-4.029817', lng: '-79.199492' },
        zoom: 20,
    });

    const location = useLocation();

    const [showAddNodeModal, setShowAddNodeModal] = useState(false);
    const [showAddRouteModal, setShowAddRouteModal] = useState(false);
    const [showAddBrigadistaModal, setShowAddBrigadistaModal] = useState(false);

    const [nodeName, setNodeName] = useState('');
    const [facultad, setFacultad] = useState('');
    // ... otros estados para los datos del nodo

    const handleAddNode = () => {
        // Lógica para enviar los datos del nodo
        setShowAddNodeModal(false);
        // ... lógica para enviar datos a backend o realizar acciones necesarias
    };

    const handleAddRoute = () => {
        // Lógica para agregar ruta
        setShowAddRouteModal(false);
        // ... lógica para enviar datos a backend o realizar acciones necesarias
    };

    const handleAddBrigadista = () => {
        // Lógica para agregar brigadista
        setShowAddBrigadistaModal(false);
        // ... lógica para enviar datos a backend o realizar acciones necesarias
    };

    return (
        <div>
            <div className="top-menu">
                <Button onClick={() => setShowAddNodeModal(true)}>Agregar Nodo</Button>
                <Button onClick={() => setShowAddRouteModal(true)}>Agregar Ruta</Button>
                <Button onClick={() => setShowAddBrigadistaModal(true)}>Agregar Brigadista</Button>
            </div>

            <MapContainer center={state.currentLocation} zoom={state.zoom}>
                <TileLayer
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    attribution="OpenStreetMap"
                />
                <Markers places={places} />
            </MapContainer>

            <Modal show={showAddNodeModal} onHide={() => setShowAddNodeModal(false)}>
                {/* Contenido del modal para agregar nodo */}
            </Modal>

            <Modal show={showAddRouteModal} onHide={() => setShowAddRouteModal(false)}>
                {/* Contenido del modal para agregar ruta */}
            </Modal>

            <Modal show={showAddBrigadistaModal} onHide={() => setShowAddBrigadistaModal(false)}>
                {/* Contenido del modal para agregar brigadista */}
            </Modal>
        </div>
    );
};

export default MapView;
