import React, { useState } from "react";
import { MapContainer, TileLayer } from "react-leaflet";
import 'leaflet/dist/leaflet.css';
import Markers from "./Markers";
import placesData from '../assets/data.json';
import { useLocation } from 'react-router-dom';
import Modal from './ModalAgregarNodo'; // Asegúrate de importar el componente Modal de tu librería de modales

const MapView = () => {
    const { places } = placesData;
    const [state, setState] = useState({
        currentLocation: { lat: '-4.029817', lng: '-79.199492' },
        zoom: 20,
        showModalAgregarNodo: false,
        showModalAgregarRuta: false,
        showModalAgregarBrigadista: false,
        // Agrega aquí los estados necesarios para almacenar la información de cada modal
    });

    const location = useLocation(); // Corrección: useLocation debe invocarse como función

    // Funciones para abrir y cerrar los modales
    const toggleModalAgregarNodo = () => {
        setState({ ...state, showModalAgregarNodo: !state.showModalAgregarNodo });
    };

    const toggleModalAgregarRuta = () => {
        setState({ ...state, showModalAgregarRuta: !state.showModalAgregarRuta });
    };

    const toggleModalAgregarBrigadista = () => {
        setState({ ...state, showModalAgregarBrigadista: !state.showModalAgregarBrigadista });
    };

    // Funciones para manejar los datos de los modales y enviar la información

    // ...

    return (
        <MapContainer center={state.currentLocation} zoom={state.zoom}>
            <TileLayer
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                attribution="OpenStreetMap"
            />
            <Markers places={places}/>

            {/* Botones para abrir los modales */}
            <button onClick={toggleModalAgregarNodo}>Agregar Nodo</button>
            <button onClick={toggleModalAgregarRuta}>Agregar Ruta</button>
            <button onClick={toggleModalAgregarBrigadista}>Agregar Brigadista</button>

            {/* Modales */}
            {state.showModalAgregarNodo && (
                Modal
            )}

            {state.showModalAgregarRuta && (
                Modal
            )}

            {state.showModalAgregarBrigadista && (
                Modal
            )}
        </MapContainer>
    );
}

export default MapView;
