import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [loggedIn, setLoggedIn] = useState(false);
    const navigate = useNavigate();

    const handleEmailChange = (e) => {
        setEmail(e.target.value);
    };

    const handlePasswordChange = (e) => {
        setPassword(e.target.value);
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        // Verifica las credenciales quemadas (hardcoded)
        if (email === 'usuario@example.com' && password === '123456') {
            // Simulando una autenticación exitosa
            setLoggedIn(true);
            console.log('¡Inicio de sesión exitoso!');
            // Redirige al usuario a la página de mapas (/map) después del inicio de sesión exitoso
            navigate('/map');
        } else {
            // Simulando autenticación fallida
            console.log('Credenciales incorrectas. Inténtalo de nuevo.');
        }
    };

    return (
        <div className="container-fluid">
            <div
                className="card p-4"
                style={{
                    backgroundImage: "url('https://via.placeholder.com/1500x1000')",
                    backgroundSize: 'cover',
                    height: '100vh',
                    display: 'flex',
                    justifyContent: 'center',
                    alignItems: 'center'
                }}
            >
                {!loggedIn ? (
                    <div>
                        <h2 className="text-center mb-4">Login</h2>
                        <form onSubmit={handleSubmit}>
                            {/* Resto del formulario de inicio de sesión */}
                        </form>
                        <div className="text-center mt-3">
                            <p>No tienes cuenta? Ingresa a las rutas de evacuación desde<Link to="/mapUsuario"> <strong>aquí</strong></Link></p>
                        </div>
                    </div>
                ) : (
                    <div className="card p-4 text-center">
                        {/* Contenido para mostrar después de iniciar sesión */}
                    </div>
                )}
            </div>
        </div>
    );
};

export default Login;
