import React,  { useState } from 'react';
import AgregarNodoModal from './AgregarNodoModal';

const MenuBar = ({ toggleModalAgregarNodo, toggleModalAgregarRuta, toggleModalAgregarBrigadista }) => {
    const [showAgregarNodoModal, setShowAgregarNodoModal] = useState(false); // Estado para controlar la visibilidad del modal

    const handleCloseAgregarNodoModal = () => setShowAgregarNodoModal(false);
    const handleShowAgregarNodoModal = () => setShowAgregarNodoModal(true);

    return (
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
            <div className="container">
                <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
                    <span className="navbar-toggler-icon"></span>
                </button>
                <a className="navbar-brand" href="#">
                    <img src="https://joinforwater.ngo/wp-content/uploads/2022/05/logo-unl-HC-01-e1651758359420.png" alt="Logo" height="90" />
                </a>
                <a className="navbar-brand d-flex align-items-center" href="#">
                    <h5 className="mb-0">Ruta de evacuaci√≥n</h5>
                </a>
                <div className="navbar-nav ml-auto">
                    <button className="nav-link btn btn-outline-primary mx-2" onClick={toggleModalAgregarNodo}>Agregar Nodo</button>
                    <button className="nav-link btn btn-outline-primary mx-2" onClick={toggleModalAgregarRuta}>Agregar Ruta</button>
                    <button className="nav-link btn btn-outline-primary mx-2" onClick={toggleModalAgregarBrigadista}>Agregar Brigadista</button>
                </div>
            </div>
        </nav>
    );
};

export default MenuBar;
