import React from 'react';
import { Modal, Button, Form } from 'react-bootstrap';

const AgregarRutaModal = ({ show, handleClose }) => {
    const nodos = [
        { nombre: 'A1', latitud: 40.7128, longitud: -74.006 },
        { nombre: 'A2', latitud: 34.0522, longitud: -118.2437 },
        { nombre: 'A3', latitud: 41.8781, longitud: -87.6298 },
        { nombre: 'B1', latitud: 51.5074, longitud: -0.1278 },
        { nombre: 'B2', latitud: 48.8566, longitud: 2.3522 },
        { nombre: 'B3', latitud: 52.5200, longitud: 13.4050 },
    ];

    const nombresNodos = nodos.map((nodo) => nodo.nombre);

    return (
        <Modal show={show} onHide={handleClose}>
            <Modal.Header closeButton>
                <Modal.Title>Agregar Ruta</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form>
                    <Form.Group controlId="formNodoOrigen">
                        <Form.Label>Nodo de Origen:</Form.Label>
                        <Form.Control as="select">
                            {nombresNodos.map((nombre, index) => (
                                <option key={index}>{nombre}</option>
                            ))}
                        </Form.Control>
                    </Form.Group>

                    <Form.Group controlId="formNodoDestino">
                        <Form.Label>Nodo de Destino:</Form.Label>
                        <Form.Control as="select">
                            {nombresNodos.map((nombre, index) => (
                                <option key={index}>{nombre}</option>
                            ))}
                        </Form.Control>
                    </Form.Group>
                </Form>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="secondary" onClick={handleClose}>
                    Cerrar
                </Button>
                {/* Botón para agregar ruta */}
                {/* <Button variant="primary" onClick={/* Función para agregar la ruta */}>
                {/*     Agregar Ruta
                {/* </Button> */}
            </Modal.Footer>
        </Modal>
    );
};

export default AgregarRutaModal;
